  <script>
    // Start The Main Tabs
    $(document).ready(function () {
      //AutoCompleteCompany('txtMainBarSearch', 'https://company.z2data.com/Home/SelectForAutoComplete', 'hdnCompanyID', RedirectToCompanyData);

      $('body').on('mouseenter', '.twoline', function () {
        var $this = $(this);

        $this.addClass('mytooltipactive').attr('title', '').tooltip({
          show: false,
          hide: false,
          position: {
            my: "left+10 middle",
            at: "right middle",
            using: function (position, feedback) {
              $(this).css(position);
              $("<div>")
                .addClass("z2tooltip-arrow")
                .addClass(feedback.vertical)
                .addClass(feedback.horizontal)
                .appendTo(this);
            }
          },
          content: $this.text()
        })
          .on('mouseleave mouseout focusout', function (event) {
            event.stopImmediatePropagation();
            if ($('.ui-tooltip:hover').length != 0) {
            } else {
              $this.removeClass('mytooltipactive').tooltip("close");
            }
          })
          .tooltip("open")
      });

      $('body').on('mouseenter', '.threeline', function () {
        var $this = $(this);

        $this.addClass('mytooltipactive').attr('title', '').tooltip({
          show: false,
          hide: false,
          position: {
            my: "left+10 middle",
            at: "right middle",
            using: function (position, feedback) {
              $(this).css(position);
              $("<div>")
                .addClass("z2tooltip-arrow")
                .addClass(feedback.vertical)
                .addClass(feedback.horizontal)
                .appendTo(this);
            }
          },
          content: $this.text()
        })
          .on('mouseleave mouseout focusout', function (event) {
            event.stopImmediatePropagation();
            if ($('.ui-tooltip:hover').length != 0) {
            } else {
              $this.removeClass('mytooltipactive').tooltip("close");
            }
          })
          .tooltip("open")
      });

      $(".z2dropdowns").on("click", function () {
        openDropdowns($(this));
      });

      $(".z2dropbg").on("click", function () {
        closeDropdowns($(this));
      });

      $(".closeChild").on("click", function () {
        closeDropdowns($(this));
        $(".z2dropbg").fadeOut("fast").removeClass("active-bg");
      });

      $(".btn-tr").on("click", function () {
        $("." + $(this).data("tr")).slideDown("fast");
      });

      $(".close-tr").on("click", function () {
        $(this).parent().parent().parent().slideUp("fast");
      })

      $("body").on("click", '.bomupload-maptableactual tr td', function (e) {
        // Get the column number
        var colnum = parseInt($(this).index()) + 1;
        // Don't clone columns if it's the first row column
        if (colnum != 1) {
          // Clone the column, including header row
          bomuploadCloneCols(colnum);
        }
      });

      $('body').on("click", '.btn-browse', function (event) {
        event.preventDefault();
        $("." + $(this).data("z2dropdown")).toggleClass("opened");
        if ($("." + $(this).data("z2dropdown")).hasClass("opened")) {
          $("." + $(this).data("z2dropdown")).slideDown(100);
          $(".active_dropbg").fadeIn("fast");
          $(".btn-browse").addClass("active-link");
          $("body").css("overflow", "hidden");
          $(".active_link").css({
            "background-color": "transparent",
            "font-weight": "300"
          });
          $(".saved-me").addClass("z");
        } else {
          $("." + $(this).data("z2dropdown")).slideUp(100);
          $(".active_dropbg").fadeOut("fast");
          $(".btn-browse").removeClass("active-link");
          $("body").css("overflow", "auto");
          $(".active_link").css({
            "background-color": "#eff4f8",
            "font-weight": "500"
          });
          $(".saved-me").removeClass("z");
        }
      });

      $('body').on("click", '.active_dropbg', function () {
        if ($(".browse_menu").hasClass("opened")) {
          $(".browse_menu").slideUp(100).removeClass("opened");
          $(".active_dropbg").fadeOut("fast");
          $(".btn-browse").removeClass("active-link");
          $("body").css("overflow", "auto");
          $(".active_link").css({
            "background-color": "#eff4f8",
            "font-weight": "500"
          });
        }
      });

      $('.bomupload-maptableactual tr td').hover(function () {
        var t = parseInt($(this).index()) + 1;
        // Ignore if the mouse is on the first row column
        if (t != 1) {
          $('td:nth-child(' + t + ')').addClass('bomupload-colhover');
          $('td:nth-child(' + t + ')').addClass('bomupload-colhover');
        }
      },
        function () {
          var t = parseInt($(this).index()) + 1;
          $('td:nth-child(' + t + ')').removeClass('bomupload-colhover');
        });
    });

    function openDropdowns(el) {
      var elem = el;
      elem.toggleClass("z2data-dropdowns");
      if (elem.hasClass("z2data-dropdowns")) {
        elem.addClass("active-dropdown-btn");
        $("." + elem.data("z2dropdownsmenus")).slideDown("fast", function () {
          $(".z2dropdowns").not(elem).removeClass("z2data-dropdowns active-dropdown-btn");
          $("." + $(".z2dropdowns").not(elem).data("z2dropdownsmenus")).slideUp("fast");
          $(".z2dropdowns").not(elem).removeClass("active-dropdown-btn");
        });
        $(".z2dropbg").fadeIn("fast").addClass("active-bg");
        if (elem.children().hasClass("myaccount")) {
          $(".user-name").css("display", "none");
          $(".myaccount").css("display", "block");
        }
        if (elem.children().hasClass("product-name")) {
          $(".product-text").css("display", "none");
          $(".product-name").css("display", "block");
        }
      } else {
        $("." + elem.data("z2dropdownsmenus")).slideUp(200, function () {
          elem.removeClass("z2data-dropdowns").removeClass("active-dropdown-btn");
        });
        $(".z2dropbg").fadeOut("fast").removeClass("active-bg");
        $(".user-name").css("display", "block");
        $(".myaccount").css("display", "none");
        $(".product-text").css("display", "block");
        $(".product-name").css("display", "none");
      }
    }

    function closeDropdowns(el) {
      var elem = el;
      var myBtn = $(".active-dropdown-btn");
      var dropdowns = $("." + myBtn.data("z2dropdownsmenus"));
      if (myBtn.hasClass("z2data-dropdowns")) {
        elem.fadeOut("fast");
        dropdowns.slideUp(200, function () {
          myBtn.removeClass("z2data-dropdowns").removeClass("active-dropdown-btn");
          myBtn.removeClass("z2data-dropdowns");
        });
        $(".user-name").css("display", "block");
        $(".myaccount").css("display", "none");
        $(".product-text").css("display", "block");
        $(".product-name").css("display", "none");
      }
    }

    function SubmitTicketing() {
      var sel_IssueType = $('#dropbg .sidepanel-sendticket .active').attr("data-valu");
      var txt_ticketSubject = $("#dropbg .sidepanel-sendticket .ticketsubject").val();
      var txt_ticketBody = $("#dropbg .sidepanel-sendticket .tickettext").val();
      if (txt_ticketSubject == '' || txt_ticketSubject == undefined) {
        toastr.error('please insert subject', 'Sent ticket');
        return false;
      }

      if (txt_ticketBody == '' || txt_ticketBody == undefined) {
        toastr.error('please insert issue', 'Sent ticket');
        return false;
      }

      var currentURL = window.location.href;

      var formData = new FormData();
      formData.append("EmailFrom", $('.usercompany').text());
      formData.append("UserID", $('#hdUserID').val());
      formData.append("IssueType", sel_IssueType);
      formData.append("ticketSubject", txt_ticketSubject);
      formData.append("ticketBody", txt_ticketBody);
      formData.append("Url", currentURL);
      formData.append("file", $("#dropbg .sidepanel-sendticket #TicketInputFile")[0].files[0]);
      StartLoader();
      $.ajax({
        cache: false,
        url: 'https://company.z2data.com/Admin/SubmitTicketInsight',
        data: formData,
        type: "Post",
        processData: false,
        contentType: false,
        error: function (res) {
          ClearData();
          $("#lblSuccessSub").addClass('text-danger');
          $("#lblSuccessSub").text('Can not send your ticket now !');
          $("#lblSuccessSub").show();
          EndLoader();
        },
        success: function (response) {
          debugger;

          if (response.success) {
            ClearData();
            toastr.success("Ticket Submited Successfully", 'Sent ticket');
            EndLoader();
            z2CloseSidePanels();
          }
          else {
            toastr.error('Cannot submit your Ticket! ', 'Sent ticket');
            EndLoader();
            z2CloseSidePanels();
          }
        }
      });
      function ClearData() {
        $("#dropbg .sidepanel-sendticket .ticketsubject").val('');
        $("#dropbg .sidepanel-sendticket .tickettext").val('');
        $("#dropbg .sidepanel-sendticket #TicketInputFile").val('');
      }
    }

    function basename(prevname) {
      return prevname.replace(/^(.*[/\\])?/, '').replace(/(\.[^.]*)$/, '');
    }

    function selectSubmitFile() {
      var _validFileExtensions = ["XLS", "XLSX", "JPEG", "BMP", "PNG", "JPG", "RAR", "ZIP", "PDF", "DOC", "DOCX"];
      var file = $("#dropbg .sidepanel-sendticket #TicketInputFile")[0].files[0];
      if (file.size > 0) {
        //Adding updates 19/09/2019
        var length = file.name.split('.').length;
        var fileExtension = (file.name.split('.')[length - 1]).toUpperCase();
        if (!_validFileExtensions.includes(fileExtension)) {
          toastr.error('Only Documents and Images and Winrar  are allowed', 'Sent ticket', { timeOut: 3000, preventDuplicates: true, positionClass: "toast-top-center" });
          $("#dropbg .sidepanel-sendticket #TicketInputFile").val('');
        } else {

          $("#dropbg .sidepanel-sendticket #FileName").val(basename(file.name));
        }
      }
    }

    $("body").on("click", '.sendticket-issuetype-each-copy', function (e) {
      $('.tickettype .sendticket-issuetype-each-copy').removeClass("active");
      $(this).addClass("active");
    });


    function RedirectToCompanyData() {
      window.location.replace("/Company/" + $("#hdnCompanyID").val());
    }

